mapper.select:
SELECT @ROWNUM:=@ROWNUM+1 AS rowNum, atregno, atregdate, atregrestdate, atregsts, atregacpt, atregcontent, atregcomment, ename, eno, edept, egrade 
FROM atregisterview, (SELECT @ROWNUM:=0) AS R

eid = #{eid}
   AND atregdate LIKE CONCAT('%', #{atregdate}, '%') 
   AND atregsts = #{atregsts} 
   AND atregacpt LIKE CONCAT('%', #{atregacpt}, '%') 
   AND atregcontent LIKE CONCAT('%', #{atregcontent}, '%') 
   AND atregcomment LIKE CONCAT('%', #{atregcomment}, '%') 

ORDER BY rowNum DESC LIMIT #{offset}, #{pageSize}

SELECT @ROWNUM:=@ROWNUM+1 AS rowNum, atregno, atregdate, atregrestdate, atregsts, atregacpt, atregcontent, ename, eno, edept, egrade, atregcomment FROM atregisterview, (SELECT @ROWNUM:=0) AS R
AND atregdate LIKE CONCAT('%', #{atregdate}, '%') AND egrade LIKE CONCAT('%', #{egrade}, '%') AND atregsts = #{atregsts} AND atregacpt LIKE CONCAT('%', #{atregacpt}, '%') AND atregcontent LIKE CONCAT('%', #{atregcontent}, '%') AND atregcomment LIKE CONCAT('%', #{atregcomment}, '%')
ORDER BY rowNum DESC LIMIT #{offset}, #{pageSize}
