mapper.select:
SELECT * 
FROM salary
WHERE eno=#{eno} AND scstrdate=#{cstrdate} AND scenddate=#{cenddate} ORDER BY sdate LIMIT 1;

SELECT * FROM contract c JOIN employee e ON e.eno=c.eno
AND cstrdate LIKE CONCAT('%', #{cstrdate}, '%') AND cenddate LIKE CONCAT('%', #{cenddate}, '%') AND edept LIKE CONCAT('%', #{edept}, '%') AND egrade LIKE CONCAT('%', #{egrade}, '%') AND estate LIKE CONCAT('%', #{estate}, '%') AND eid LIKE CONCAT('%', #{eid}, '%') AND ename LIKE CONCAT('%', #{ename}, '%') AND cagree LIKE CONCAT('%', #{cagree}, '%')
ORDER BY cno DESC LIMIT 0, 10;
